class Cloth(object):
    cell = None
    x = 0
    y = 0
    def __init__(self, x, y):
        self.cell = list([list([0 for i in range(y)]) for i in range(x)])
        self.x = x
        self.y = y
    def check(self, x, y):
        try:
            self.cell[x][y] += 0
            return True
        except IndexError:
            return False
    def point(self, x, y):
        try:
            self.cell[x][y] += 1
        except IndexError:
            pass
    def small(self, x, y):
        if not self.check(x, y):
            return
        self.point(x, y + 1)
        self.point(x - 1, y)
        self.point(x, y)
        self.point(x + 1, y)
        self.point(x, y - 1)
    def medium(self, x, y):
        if not self.check(x, y):
            return
        self.small(x, y)
        self.point(x + 1, y + 1)
        self.point(x - 1, y + 1)
        self.point(x + 1, y - 1)
        self.point(x - 1, y - 1)
    def large(self, x, y):
        if not self.check(x, y):
            return
        self.medium(x, y)
        self.point(x, y + 2)
        self.point(x - 2, y)
        self.point(x + 2, y)
        self.point(x, y - 2)
cloth = Cloth(10, 10)
while True:
    try:
        (x, y, s) = map(lambda x:int(x)-1, raw_input().split(','))
        if s == 0:
            cloth.small(x, y)
        elif s == 1:
            cloth.medium(x, y)
        elif s == 2:
            cloth.large(x, y)
        else:
            break
    except EOFError:
        break
print reduce(lambda x,y:x+y,map(lambda x:x.count(0),cloth.cell))
