				if (ng(ny, nx)) continue;
				field[ny][nx]++;
			}
		}
		if (size == 3) {
			REP(ny, y-1, y+2) REP(nx, x-1, x+2) {
				if (ng(ny, nx)) continue;
				field[ny][nx]++;
			}
			rep(i, 4) {
				int ny = y + dy[i]*2;
				int nx = x + dx[i]*2;
				if (ng(ny, nx)) continue;
				field[ny][nx]++;
			}
		}
	}
	int brank_n = 0;
	int max_value = 0;
	rep(y, 10) rep(x, 10) {
		if (field[y][x] == 0) brank_n++;
		if (field[y][x] > max_value) max_value = field[y][x];
	}
	cout << brank_n << endl << max_value << endl;


	return 0;
}

