				exist(x, y);
				exist(x-1, y);
				exist(x, y-1);
				exist(x+1, y);
				exist(x, y+1);
				break;
			case 2:
				for(i = 0; i < 3; i++){
					for(j = 0; j < 3; j++){
						exist(x+i-1, y+j-1);
					}
				}
				break;
			case 3:
				for(i = 0; i < 3; i++){
					for(j = 0; j < 3; j++){
						exist(x+i-1, y+j-1);
					}
				}

