    n=0; h=0; k=0;
	for (i=2;i<12;i++) for (j=2;j<12;j++) {
		if (m[i][j]==0) k++; else h=h<m[i][j] ? m[i][j] : h;
	}
    cout << k << endl << h << endl;
	return 0;
	}

