void big(int a[10][10],int x,int y){
	mid(a,x,y);
	a[x+2][y]++;
	a[x-2][y]++;
	a[x][y+2]++;
	a[x][y-2]++;
}
void mid(int a[10][10],int x,int y){
	sma(a,x,y);
	a[x+1][y+1]++;
	a[x+1][y-1]++;
	a[x-1][y+1]++;
	a[x-1][y-1]++;
}
void sma(int a[10][10],int x,int y){
	a[x][y]++;
	a[x+1][y]++;
	a[x-1][y]++;
	a[x][y+1]++;
	a[x][y-1]++;
}

#include <iostream>
using namespace std;

int main(){
  int x,y,size;
  int canvas[10][10];
  int empty_num, max_val;
  int i, j;

  while(cin>>x){
    for(i=0; i<10; i++)
      for(j=0; j<10; j++) canvas[i][j]=0;
    cin>>y; cin>>size;

    for(i=-1; i<=1; i+2)
      if(x+i>=0 && x+i<10) canvas[x+i][y]++;
    for(i=-1; i<=1; i+2)
      if(y+i>=0 && y+i<10) canvas[x][y+i]++;
    if(size>1){
      for(i=-1; i<=1; i+2){
        for(j=-1; j<=1; j+2)
          if(x+i>=0 && x+i<10 && y+j>=0 && y+j<10) canvas[x+i][y+j]++;
      }
    }
    if(size>2){
      for(i=-2; i<=2; i+4){
        for(j=-2; j<=2; j+4)
          if(x+i>=0 && x+i<10 && y+j>=0 && y+j<10) canvas[x+i][y+j]++;
      }
    }

    empty_num=0; max_val=0;
    for(i=0; i<10; i++){
      for(j=0; j<10; j++){
        if(canvas[i][j]>max_val) max_val=canvas[i][j];
        else if(canvas[i][j]==0) empty_num++;
      }
    }

    printf("%d\n%d\n", empty_num, max_val);
  }
  return 0;
}

