int x,y,size;
int paper[14][14]={0};

int main(){

  while(scanf("%d,%d,%d",&x,&y,&size)!=EOF){
    switch(size){
    case 1:
      small_size();
      break;
    case 2:
      small_size();
      middle_size();
      break;
    case 3:
      small_size();
      middle_size();
      big_size();
      break;
    }

  }

  solve();

  return 0;
}

void small_size(){

  paper[y+2][x+2]++;
  paper[y+1][x+2]++;
  paper[y+2][x+1]++;
  paper[y+2][x+3]++;
  paper[y+3][x+2]++;

}
void middle_size(){

  paper[y+1][x+1]++;
  paper[y+3][x+1]++;
  paper[y+1][x+3]++;
  paper[y+3][x+3]++;

}

void big_size(){

  paper[y+2][x]++;
  paper[y][x+2]++;
  paper[y+2][x+4]++;
  paper[y+4][x+2]++;

}

void solve(){

  int i,j;
  int zero_cnt=0;
  int max=0;

  for(i=2;i<12;i++){
    for(j=2;j<12;j++){

      if(paper[i][j]==0){
	zero_cnt++;
      }

      if(max<paper[i][j]){
	max=paper[i][j];
      }
    }
  }

  for(i=2;i<12;i++){
    for(j=2;j<12;j++){
      printf("%d ",paper[i][j]);
    }
    printf("\n");
  }

  printf("%d\n%d\n",zero_cnt,max);

}

#include <cstdio>
#include <algorithm>
using namespace std;

int field[10][10], x, y, s, cnt, ma;

void s1()
{
    if (y - 1 >= 0) field[x][y-1]++;
    if (x - 1 >= 0) field[x-1][y]++;
    if (y + 1 < 10) field[x][y+1]++;
    if (x + 1 < 10) field[x+1][y]++;
}

void s2()
{
    s1();
    if (x - 1 >= 0 && y - 1 >= 0) field[x-1][y-1]++;
    if (x - 1 >= 0 && y + 1 < 10) field[x-1][y+1]++;
    if (x + 1 < 10 && y - 1 >= 0) field[x+1][y-1]++;
    if (x + 1 < 10 && y + 1 < 10) field[x+1][y+1]++;
}

void s3()
{
    s2();
    if (y - 2 >= 0) field[x][y-2]++;
    if (x - 2 >= 0) field[x-2][y]++;
    if (y + 2 < 10) field[x][y+2]++;
    if (x + 2 < 10) field[x+2][y]++;
}

int main()
{
    while(scanf("%d,%d,%d",&x,&y,&s) != EOF) {
        field[x][y]++;

        if      (s == 1) s1();
        else if (s == 2) s2();
        else if (s == 3) s3();

        for(int i=0;i < 10;++i) {
            for(int j=0;j < 10;++j) {
                if (!field[i][j]) ++cnt;
                else ma = max(ma, field[i][j]);
            }
        }
    }

    printf("%d\n%d\n", cnt, ma);

    return 0;
}

