		x += 2;
		y += 2;
		if(size == 1){
			for(i = -1,mass[x][y]--;i < 2;i++){
				mass[x+i][y]++;
				mass[x][y+i]++;
			}
		}else if(size == 2){
			for(i = -1;i < 2;i++){
				for(j = -1;j < 2;j++){
					mass[x+i][y+j]++;
				}
			}
		}else{
			for(i = -1;i < 2;i++){
				for(j = -1;j < 2;j++){
					mass[x+i][y+j]++;
				}
			}

			for(i = -2;i <= 2;i += 4){
				mass[x+i][y]++;
				mass[x][y+i]++;
			}
		}
	}

	for(i = 2;i < 12;i++){
		for(j = 2;j < 12;j++){
			if(mass[j][i] == 0){
				n++;
			}else if(mass[j][i] > max){
				max = mass[j][i];
			}
		}
	}

	printf("%d\n",n);
	printf("%d\n",max);

	return 0;
}

