			for(int i=-1;i<=1;i++){
				for(int j=-1;j<=1;j++){
					if(i==0&&j==0) continue;
					else if(x+i < 0 && x+i > 10 && y+j < 0 && y+j > 10) continue;
					else {
						map[x+i][y+j] += 1;
						if(i == -1 && j == 0){
							if(x+i+i > -1 && y+j+j > -1){
								map[x+i+i][y+j+j]++;
							}
						}
