		int b = char_to_int(str[2]);
		int s = char_to_int(str[4]);
		p[a][b]++;
		if (s >= 1){
			p[a - 1][b]++;
			p[a + 1][b]++;
			p[a][b - 1]++;
			p[a][b + 1]++;
			if (s >= 2) {
				p[a - 1][b + 1]++;
				p[a + 1][b - 1]++;
				p[a - 1][b - 1]++;
				p[a + 1][b + 1]++;
				if (s >= 3) {
					p[a - 2][b]++;
					p[a + 2][b]++;
					p[a][b - 2]++;
