#include<iostream>
#include<cstdio>
#include<cstdlib>
#include<algorithm>
using namespace std;

int paper[10][10];

bool onPaper(int x,int y){
	return 0<=x&&x<10&&0<=y&&y<10;
}
void small(int x,int y){
	for(int i=-1;i<=1;i++)
		for(int j=-1;j<=1;j++)
			if(onPaper(x+i,y+j) && abs(i)+abs(j) <= 1)
				paper[x+i][y+j]++;
}
void medium(int x,int y){
	for(int i=-1;i<=1;i++)
		for(int j=-1;j<=1;j++)
			if(onPaper(x+i,y+j) && abs(i)<=1 && abs(j)<=1)
				paper[x+i][y+j]++;
}
void large(int x,int y){
	for(int i=-2;i<=2;i++)
		for(int j=-2;j<=2;j++)
			if(onPaper(x+i,y+j) && abs(i)+abs(j) <= 2)
				paper[x+i][y+j]++;
}
int main(){
	int x,y,size;
	while(scanf("%d,%d,%d",&x,&y,&size)!=EOF){
		if(size==1)small(x,y);
		else if(size==2)medium(x,y);
		else large(x,y);
	}
	int n=0,depth=0;
	for(x=0;x<10;x++){
		for(y=0;y<10;y++){
			if(paper[x][y]==0)n++;
			depth = max(depth,paper[x][y]);
		}
	}
	cout<<n<<endl;
	cout<<depth<<endl;
	return 0;
}

