int peper[11][11];

void paint(int x, int y)
{
	if (x * y >= 0 && x >= 0){
		peper[x][y]++;
	}
}

int main()
{
	int x, y;
	int size;

	while (scanf("%d,%d,%d", &x, &y, &size) != EOF){
		peper[y][x]++;

		paint(y - 1, x);
		paint(y + 1, x);
		paint(y, x - 1);
		paint(y, x + 1);

		if (size > 1){
			paint(y - 1, x + 1);
			paint(y + 1, x + 1);
			paint(y + 1, x - 1);
			paint(y - 1, x - 1);
		}
		if (size > 2){
			paint(y - 2, x);
			paint(y + 2, x);
			paint(y, x - 2);
			paint(y, x + 2);
		}
	}

	int dark = 0;
	int blank = 0;

	for (int i = 0; i < 10; i++){
		for (int j = 0; j < 10; j++){
			if (peper[j][i] == 0)	blank++;
			dark = max(dark, peper[j][i]);
		}
	}
	printf("%d\n%d\n", blank, dark);
	return (0);
}

