void s1(int y, int x){
  p[y][x]++;
  if(a(y+1, x))p[y+1][x]++;
  if(a(y, x+1))p[y][x+1]++;
  if(a(y-1, x))p[y-1][x]++;
  if(a(y, x-1))p[y][x-1]++;
}

void s2(int y, int x){
  s1(y, x);
  if(a(y+1, x+1))p[y+1][x+1]++;
  if(a(y+1, x-1))p[y+1][x-1]++;
  if(a(y-1, x+1))p[y-1][x+1]++;
  if(a(y-1, x+1))p[y-1][x+1]++;
}

void s3(int y, int x){
  s2(y, x);
  if(a(y+2, x))p[y+2][x]++;
  if(a(y-2, x))p[y-2][x]++;
  if(a(y, x+2))p[y][x+2]++;
  if(a(y, x-2))p[y][x-2]++;
}

