	int dx[] = { 1, 0, -1, 0 }, dy[] = { 0, 1, 0, -1 };
	rep(i, 4){
		int nx = x + dx[i], ny = y + dy[i];
		if (nx >= 0 && ny >= 0 && nx < 10 && ny < 10){
			table[nx][ny]++;
		}
	}
	table[x][y]++;
}

void pnt2(int x, int y){
	int dx[] = { 1, 1, -1, -1 }, dy[] = { 1, -1, 1, -1 };
	pnt1(x, y);
	rep(i, 4){
		int nx = x + dx[i], ny = y + dy[i];
		if (nx >= 0 && ny >= 0 && nx < 10 && ny < 10){
			table[nx][ny]++;
		}
	}
}

void pnt3(int x, int y){
	int dx[] = { 2, 0, -2, 0 }, dy[] = { 0, 2, 0, -2 };
	pnt2(x, y);
	rep(i, 4){
		int nx = x + dx[i], ny = y + dy[i];
		if (nx >= 0 && ny >= 0 && nx < 10 && ny < 10){
			table[nx][ny]++;
		}
	}
}

