int mas[10][10]={{0}};
void dot(int x,int y){
  if(x>=0&&y>=0&&x<=9&&y<=9) mas[y][x]++;
}
void small(int x,int y){
  dot(x,y);
  dot(x+1,y);
  dot(x,y+1);
  dot(x-1,y);
  dot(x,y-1);
}
void mid(int x,int y){
  small(x,y);
  dot(x+1,y+1);
  dot(x-1,y-1);
  dot(x+1,y-1);
  dot(x-1,y+1);
}
void big(int x,int y){
  mid(x,y);
  dot(x+2,y);
  dot(x-2,y);
  dot(x,y+2);
  dot(x,y-2);
}
int main(){
