void solve() {
  vector<vi32> board(10, vi32(10));

  auto check = [](int x, int y) {
    if (x < 0 || x >= 10 || y < 0 || y >= 10) return false;
    return true;
  };

  int x, y, s;
  char c1, c2;
  while (cin >> x >> c1 >> y >> c2 >> s) {
    if (s == 1) {
      board[x][y]++;
      rep(i, 4) {
        int dx = x + (i - 1) % 2;
        int dy = y + (i - 2) % 2;
        if (!check(dx, dy)) continue;
        board[dx][dy]++;
      }
    } else if (s == 2) {
      for (int i = -1; i <= 1; i++)
      for (int j = -1; j <= 1; j++) {
        int dx = x + i;
        int dy = y + j;
        if (!check(dx, dy)) continue;
        board[dx][dy]++;
      }
    } else {
      for (int i = -2; i <= 2; i++)
      for (int j = -2; j <= 2; j++) {
        if (abs(i) + abs(j) > 2) continue;
        int dx = x + i;
        int dy = y + j;
        if (!check(dx, dy)) continue;
        board[dx][dy]++;
      }
    }
  }

  int empty = 0, ma = 0;
  rep(i, 10) rep(j, 10) amax(ma, board[i][j]);
  rep(i, 10) rep(j, 10) {
    if (board[i][j] == 0) empty++;
  }
  cout << empty << '\n';
  cout << ma << '\n';
}

