    for (int i = 0; i < 10; i++)
    {
        for (int j = 0; j < 10; j++)
        {
            if(a[i][j] > 0)
            {
                count = count+1;
            }

            if (a[i][j] > density)
            {
                density = a[i][j];
            }
        }
    }


    cout<<100 - count<<endl;
    cout<<density<<endl;

}

