        switch (s) {
            case 1: //small
                drop(x-1,y);
                drop(x,y-1);
                drop(x+1,y);
                drop(x,y+1);
                drop(x,y);
                break;

            case 3:
                drop(x+2,y);
                drop(x,y+2);
                drop(x-2,y);
