#include <cstdio>
#include <cstring>
#include <algorithm>
using namespace std;
int imap[11][11];
int dx[4] = {-1, 0, 1, 0};
int dy[4] = {0, -1, 0, 1};
int maxv, sum;

void color(const int &x, const int &y, const int &t) {
    int nx = x, ny = y;
    if (t == 1) {
        imap[x][y]++;
        for (int i = 0; i < 4; i++) {
            nx=x+dx[i], ny=y+dy[i];
            if (nx >= 0 && nx < 10 && ny >= 0 && ny < 10) imap[nx][ny]++;
        }
        return;
    }
    if (t == 2 || t == 3) {
        imap[x][y]++;
        for (int i = -1; i <= 1; i++) {
            for (int j = -1; j <= 1; j++) {
                if (i == 0 && j == 0) continue;
                nx=x+i, ny=y+j;
                if (nx >= 0 && nx < 10 && ny >= 0 && ny < 10) imap[nx][ny]++;
            }
        }
        if (t == 2) return;
    }
    if (t == 3) {
        for (int i = 0; i < 4; i++) {
            nx=x+dx[i]*2, ny=y+dy[i]*2;
            if (nx >= 0 && nx < 10 && ny >= 0 && ny < 10) imap[nx][ny]++;
        }
        return;
    }
}

void calc() {
    maxv = sum = 0;
    for (int i = 0; i < 10; i++) {
        for (int j = 0; j < 10; j++) {
            if (imap[i][j] == 0) sum++;
            else maxv = max(imap[i][j], maxv);
        }
    }
}

int main(){
    memset(imap, 0, sizeof (imap));
    int x, y, t;
    while(scanf("%d,%d,%d", &y, &x, &t) != EOF) {
        color(x, y, t);
    }
    calc();

    /*for (int i = 0; i < 10; i++) {
        for (int j = 0; j < 10; j++) printf("%d ", imap[i][j]);
        printf("\n");
    }*/
    printf("%d\n%d\n", sum, maxv);
    return 0;
}

