typedef long long llong;
using namespace std;

int paper[10][10] = { 0 };

void add(int x, int y) {
	if (x >= 0 && y >= 0 && x < 10 && y < 10)paper[x][y]++;
	return;
}

int small(int x, int y) {
	add(x, y);
	add(x + 1, y); add(x - 1, y);
	add(x, y + 1); add(x, y - 1);
	return 0;
}

int medi(int x, int y) {
	for (int i = x - 1; i <= x + 1; i++) {
		for (int j = y - 1; j <= y + 1; j++) {
			add(i, j);
		}
	}
	return 0;
}

int large(int x, int y) {
	add(x, y + 2); add(x, y - 2);
	for (int i = x - 1; i <= x + 1; i++) {
		add(i, y + 1); add(i, y - 1);
	}
	for (int i = x - 2; i <= x + 2; i++) {
		add(i, y);
	}
	return 0;
}
int main() {
	int x, y, s;

	while (cin >> x&&getchar()&&cin >> y&&getchar()&&cin >> s) {
		if (s == 1)small(x, y);
		else if (s == 2)medi(x, y);
		else if (s == 3)large(x, y);
	}

	int cnt = 0; int maxim = 0;
	for (int i = 0; i < 10; i++) {
		for (int j = 0; j < 10; j++) {
			if (paper[i][j] == 0)cnt++;
			if (paper[i][j] > maxim)maxim = paper[i][j];

		}
	}
	cout << cnt << endl << maxim << endl;

	return 0;
}

