  default:
    paper[x][y]++;
    if(x > 0)           paper[x-1][y]++;
    if(x < XSIZ-1)      paper[x+1][y]++;
    if(y > 0)           paper[x][y-1]++;
    if(y < YSIZ-1)      paper[x][y+1]++;
  }
