}

#include <stdio.h>

void di(int map[10][10], int x, int y)
{
	int i, j;
	for (i = y - 1; i <= y + 1; i++){
		for (j = x - 1; j <= x + 1; j++){
			if (0 <= i && i < 10 && 0 <= j && j < 10){
				map[i][j]++;
			}
		}
	}
}

int main(void)
{
	int x, y, s;
	int wc = 0, max_i = 0;
	int map[10][10] = {0};

	while (scanf("%d,%d,%d", &x, &y, &s) != EOF){
		switch (s){
		  case 1:
		  	di(map, x, y);
			map[y - 1][x - 1]--;
			map[y - 1][x + 1]--;
			map[y + 1][x - 1]--;
			map[y + 1][x + 1]--;
			break;

		  case 2:
		  	di(map, x, y);
			break;

		  case 3:
		  	di(map, x, y);
			map[y - 2][x]++;
