#include <stdio.h>

#define PUT(f, x, y) (0 <= x && x < 10 && 0 <= y && y < 10 ? f[y][x]++ : 0)
#define DISTANCE(x, y, x2, y2) (abs(x-x2)+abs(y-y2))
#define DIST_MAX(x, y, x2, y2) (abs(x-x2) > abs(y-y2) ? abs(x-x2) : abs(y-y2))

main(){
  int x, y, type, cnt, max, bx, by;
  int f[10][10];

  for (y = 0; y < 10; y++) {
    for (x = 0; x < 10; x++) {
      f[y][x] = 0;
    }
  }

  while (scanf("%d,%d,%d", &bx, &by, &type) > 0) {
    if (type == 3) {
      for (y = by - 2; y <= by + 2; y++) {
	for (x = bx - 2; x <= bx + 2; x++) {
	  if (DISTANCE(x, y, bx, by) <= 2) {
	    PUT(f, x, y);
	  }
	}
      }
    } else if (type == 2) {
      for (y = by-1; y <= by+1; y++) {
	for (x = bx-1; x <= bx+1; x++) {
	  if (DIST_MAX(x, y, bx, by) <= 1) {
	    PUT(f, x, y);
	  }
	}
      }
    } else {
      PUT(f, bx, by);
      PUT(f, bx, by-1);
      PUT(f, bx, by+1);
      PUT(f, bx-1, by);
      PUT(f, bx+1, by);
    }
  }

  max = 0;
  cnt = 0;
  for (y = 0; y < 10; y++) {
    for (x = 0; x < 10; x++) {
      if (f[y][x] == 0) cnt++;
      if (f[y][x] > max) max = f[y][x];
    }
  }
  printf("%d\n", cnt);
  printf("%d\n", max);

  return 0;
}

