void mapping(int x,int y,int size){

	map[x][y]++;
	switch(size){
		case 1:
			if(x!=0) map[x-1][y]++;
			if(x!=9) map[x+1][y]++;
			if(y!=0) map[x][y-1]++;
			if(y!=9) map[x][y+1]++;
			break;
		case 2:
			if(x!=0){
				map[x-1][y]++;
				if(y!=0) map[x-1][y-1]++;
			}
			if(x!=9){
				map[x+1][y]++;
				if(y!=9) map[x+1][y+1]++;
			}
			if(y!=0){
				map[x][y-1]++;
				if(x!=9) map[x+1][y-1]++;
			}
			if(y!=9){
				map[x][y+1]++;
				if(x!=0) map[x-1][y+1]++;

			}
			break;
		case 3:
			if(x!=0){
				map[x-1][y]++;
				if(y!=0) map[x-1][y-1]++;
				if(x!=1) map[x-2][y]++;
