const int dx[] = {1, 0, -1, 0, -1, -1, 1, 1};


void fillS(int y, int x);
void fillM(int y, int x);
void fillL(int y, int x);

int main() {
  int x, y, s;
  while(scanf("%d,%d,%d", &x, &y, &s) != EOF) {
    if(s == 1) fillS(y, x);
    else if(s == 2) fillM(y, x);
    else if(s == 3) fillL(y, x);
  }
  {
    int cnt = 0;
    int mx = 0;
