#define W 10
#define H 10

int map[W][H];

void inc( int x, int y ){
	if( x>=0 && x<W && y>=0 && y<H )
		map[x][y]++;
}

void drop_ink( int x, int y, int size){
	int vh  = size/2 + size%2;
	int dia = size/2;
	int i;

	for( i=-vh; i<=vh; i++ ){
		if( i==0 )continue; //avoid duplication
		inc( x+i,  y  );
		inc(  x , y+i );
	}
	for( i=-dia; i<=dia; i++ ){
		if( i==0 )continue;//avoid duplication
		inc( x+i, y+i );
		inc( x-i, y+i );
	}
	inc( x, y );
}

