		*(tbl - 10) += 1;
	if(center <= 90)//centerが最下列でない
		*(tbl + 10) += 1;
	if(place != 0)//centerが左側でない
		*(tbl - 1) += 1;
	if(place != 9)//centerが右側でない
		*(tbl + 1) += 1;
	if(size >= 2)
