int paper[10][10];

void add(int x, int y)
{
	if( x < 0 || x > 9 || y < 0 || y > 9 )return;
	paper[x][y]++;
}

void smallAdd(int x, int y)
{
	add( x, y );
	add( x - 1, y     );
	add( x    , y - 1 );
	add( x + 1, y     );
	add( x    , y + 1 );
}

void midAdd(int x, int y)
{
	smallAdd(x, y);
	add( x - 1, y - 1 );
	add( x + 1, y - 1 );
	add( x - 1, y + 1 );
	add( x + 1, y + 1 );
}

void bigAdd(int x, int y)
{
	midAdd(x, y);
	add( x    , y - 2 );
	add( x - 2, y     );
	add( x    , y + 2 );
	add( x + 2, y     );
}

void drop(int x, int y, int size)
{
	int i;
	int j;
	switch( size )
	{
		case 1:
			smallAdd( x, y );
			break;
		case 2:
			midAdd( x, y );
			break;
		case 3:
			bigAdd( x, y );
			break;
	}
}

