                paper[x-1][y+1]++;
                paper[x+1][y-1]++;
                paper[x+1][y+1]++;
            }
            if(size>2)
            {
                paper[x-2][y]++;
                paper[x+2][y]++;
                paper[x][y-2]++;
                paper[x][y+2]++;
            }
        }
    }
    for(i=0;i<10;i++)
        for(j=0;j<10;j++)
        {
            if(paper[i][j]==0)
