void dropInk(int x, int y, int size)
{
        switch(size) {
                case 1:
                        dropSInk(x, y);
                        break;
                case 2:
                        dropMInk(x, y);
                        break;
                case 3:
                        dropLInk(x, y);
                        break;
        }
}

void dropSInk(int x, int y)
{
        addInk(x, y);
        addInk(x-1, y);
        addInk(x+1, y);
        addInk(x, y-1);
        addInk(x, y+1);
}

void dropMInk(int x, int y)
{
        addInk(x-1, y-1);
        addInk(x-1, y);
        addInk(x-1, y+1);
        addInk(x, y-1);
        addInk(x, y);
        addInk(x, y+1);
        addInk(x+1, y-1);
        addInk(x+1, y);
        addInk(x+1, y+1);
}

void dropLInk(int x, int y)
{
        addInk(x, y);
        addInk(x, y-2);
        addInk(x-1, y-1);
        addInk(x, y-1);
        addInk(x+1, y-1);
        addInk(x-2, y);
        addInk(x-1, y);
        addInk(x+1, y);
        addInk(x+2, y);
        addInk(x-1, y+1);
        addInk(x, y+1);
        addInk(x+1, y+1);
        addInk(x, y+2);
}

void addInk(int x, int y)
{
        if(0 <= x && x < PAPER_SIZE &&
                        0 <= y && y < PAPER_SIZE) {
                paper[y][x]++;
        }
}

int countWhite()
{
        int white = 0;
        int i, j;
        for(i = 0; i < PAPER_SIZE; i++) {
                for(j = 0; j < PAPER_SIZE; j++) {
                        if(paper[i][j] == 0)
                                white++;
                }
        }
        return white;
}

int checkMax()
{
        int max = 0;
        int i, j;
        for(i = 0; i < PAPER_SIZE; i++) {
                for(j = 0; j < PAPER_SIZE; j++) {
                        if(max < paper[i][j])
                                max = paper[i][j];
                }
        }
        return max;
}

