int main()
{
	int ink[10][10]={0};/*インクの量*/
	int x,y,n,i,j,k[2],l[2],max=0,none=0;
	int size;/*インクのタイプ*/

	while(scanf("%d,%d,%d",&x,&y,&size) != EOF)
	{
		n=0;
		ink[x][y]+=1;/*中心に足す*/

		while(n<size)/*タイプ1から順に処理する*/
		{
			n++;
			j=1;
			i=(n<3)?1:2;

			while(1)
			{
				k[0]=x+i;
				k[1]=x+j*((i+1)%2);
				l[0]=y+j*((i+1)%2);
				l[1]=y+i;

				if(k[0]>=0 && l[0]>=0)
					ink[k[0]][l[0]]+=1;

				if(k[1]>=0 && l[1]>=0)
					ink[k[1]][l[1]]+=1;

				i*=-1;
				j*=-1;
				if(j>=0)
					break;/*2回ループ*/
			}
		}
	}

		for(i=0;i<10;i++)
		{
			for(j=0;j<10;j++)
			{
				if(ink[i][j]==0)
				{
					none++;
				}
				else if(max<ink[i][j])
				{
					max=ink[i][j];
				}

			}
		}

		printf("%d\n",none);
		printf("%d\n",max);

		return 0;
}

