void small(int x, int y, int ppr[X][Y])
{
        ppr[x + 1][y]     += 1;
        ppr[x]    [y]     += 1;
        ppr[x - 1][y]     += 1;
        ppr[x]    [y + 1] += 1;
        ppr[x]    [y - 1] += 1;
}

