//真っ白な紙にインクを垂らしインク絵汚れていない部分の数を出力し、汚れる回数が多いエリアを出力する。
//落とすインク液は少しならば１、中ぐらいなら２、多いならば３とする。
#include<stdio.h>

void littleDrop(int,int,int *,int);
void midiumDrop(int,int *,int);
void largeDrop(int,int *);


void largeDrop(int center,int *tbl){
	if(center >= 20)
		*(tbl - 20) += 1;
	if(center % 10 >= 2)
		*(tbl - 2) += 1;
	if(center % 10 <= 7)
		*(tbl + 2) += 1;
	if(center <= 80)
		*(tbl + 20) += 1;
}

void midiumDrop(int center,int *tbl ,int size){
//真ん中と八方に+1//
	if(center % 10 != 0)
		*(tbl - 11) += 1;
	if(center % 10 != 9)
		*(tbl - 9) += 1;
	if(center % 10 != 0)
		*(tbl + 9) += 1;
	if(center % 10 != 9) 
		*(tbl + 11) += 1;
	if(size == 3)
		largeDrop(center,tbl);
}


void littleDrop(int x ,int y ,int *tbl,int size){
//インクを落とす場所は左上(0,0)から何番目かを求める。
	int center = x + y * 10;
//(0,0)から中央へと移動させる
	tbl = (tbl + center);

//真ん中に+1
	*(tbl) += 1;
//上に+1
	if(center >= 10)
		*(tbl - 10) += 1;
	if(center <= 90)
		*(tbl + 10) += 1;
	if(center % 10 != 0)
		*(tbl - 1) += 1;
	if(center % 10 != 9)
		*(tbl + 1) += 1;
	if(size >= 2)
		midiumDrop(center, tbl, size);
}



int main(void){
	int i,j,clear=0,max=0;
	int x,y,size;
	int tbl[10][10] = {0};

	while(scanf("%d,%d,%d",&x ,&y ,&size) != EOF){
		if(x >= 0 && y >= 0 && size >= 0 && size <= 3 && x <= 9 && y <= 9)
			littleDrop(x,y,*tbl,size);
	}

	for(i = 0 ; i < 10 ;i ++){
		for(j = 0; j < 10; j ++){
			if(tbl[i][j] == 0)
				clear ++;
			if(tbl[i][j] > max)
				max = tbl[i][j];
		}
	}
	printf("%d\n",clear);
	printf("%d\n",max);
}

