void drop(int x,int y,int s){
	int i,l;

	for(i=x-1;i<=x+1;i++){
		if(i<0 || i>9)continue;
		for(l=y-1;l<=y+1;l++){
			if(l<0 || l>9)continue;
			if(s==1 && (i!=x && l!=y))continue;
			m[i][l]++;
		}
	}

	if(s==3){
		for(i=x-2;i<=x+2;i+=4){
			if(i<0 || i>9)continue;
			for(l=y-2;l<=y+2;l+=4){
				if(l<0 || l>9)continue;
				m[i][l]++;
			}
		}
	}

	return;
}

