        for (j = 0; j < 10; j++) {
            board[i][j] = 0;
        }
    }
    while (scanf("%d,%d,%d", &x, &y, &size) != EOF) {
        board[x][y]++;
        if (size == 1) {
            for (i = 0; i < 4; i++) {
                if (0 <= x + dx[i] && x + dx[i] < 10 
                        || 0 <= y + dy[i] && y + dy[i] < 10) {
                    board[x+dx[i]][y+dy[i]]++;
                }
            }
        } else if (size == 2) {
