void drop(int x,int y,int s);

int m[10][10];

int main(void){
	int i,l,x,y,s,max=0,ans=0;

	for(i=0;i<10;i++){
		for(l=0;l<10;l++){
			m[i][l]=0;
		}
	}

	while(scanf("%d,%d,%d",&x,&y,&s)!=EOF){
		drop(x,y,s);
	}


	for(i=0;i<10;i++){
		for(l=0;l<10;l++){
			if(m[i][l]==0)ans++;
			if(max<m[i][l])max=m[i][l];
		}
	}

	printf("%d\n%d\n",ans,max);


	return 0;


}

void drop(int x,int y,int s){
	int i,l;

	for(i=x-1;i<=x+1;i++){
		if(i<0 || i>9)continue;
		for(l=y-1;l<=y+1;l++){
			if(l<0 || l>9)continue;
			if(s==1 && (i!=x && l!=y))continue;
			m[i][l]++;
		}
	}
	if(s==3){
		i=x-2;
		if(i>=0)m[i][y]++;
		i=x+2;
		if(i<=9)m[i][y]++;
		l=y-2;
		if(l>=0)m[x][l]++;
		l=y+2;
		if(l<=9)m[x][l]++;
	}
	return;
}

