				k[1]=x+j*((i+1)%2);
				l[0]=y+j*((i+1)%2);
				l[1]=y+i;

				if(k[0]>=0 && l[0]>=0)
					ink[k[0]][l[0]]+=1;

				if(k[1]>=0 && l[1]>=0)
