//真っ白な紙にインクを垂らしインク絵汚れていない部分の数を出力し、汚れる回数が多いエリアを出力する。
//落とすインク液は少しならば１、中ぐらいなら２、多いならば３とする。
#include<stdio.h>

void littleDrop(int,int,int *,int);
void midiumDrop(int,int *,int,int);
void largeDrop(int,int *,int);
void showArray(int [][10]);

void largeDrop(int center,int *tbl,int place){
	if(center >= 20)//二つ上に配列があるか確認
		*(tbl - 20) += 1;
	if(place >= 2)//二つ左に配列があるか確認
		*(tbl - 2) += 1;
	if(place <= 7)//二つ右に配列があるか確認
		*(tbl + 2) += 1;
	if(center <= 80)//二つ下に配列がある確認
		*(tbl + 20) += 1;
}

void midiumDrop(int center,int *tbl ,int size, int place){
//右上、右下、左上、左下に+1//
	if(center > 10){//上に配列がある
		if(place != 0)//左に配列があるか確認
			*(tbl - 11) += 1;
		if(place != 9)//右に配列があるか確認
			*(tbl - 9) += 1;
	}
	if(center < 90){//下に配列がある
		if(place != 0)//左に配列があるか確認
			*(tbl + 9) += 1;
		if(place != 9)//右に配列があるか確認
			*(tbl + 11) += 1;
	}
	if(size == 3)
		largeDrop(center,tbl,place);
}

void littleDrop(int x ,int y ,int *tbl,int size){
//インクを落とす場所は左上(0,0)から何番目かを求める。
	int center = x + y * 10;
	int place = center % 10;//左、右に配列があるか確認するときに使用
//(0,0)からインクの落下位置へと移動させる
	tbl = (tbl + center);

//真ん中に+1
	*(tbl) += 1;
	if(center >= 10)//centerが最上列でない
		*(tbl - 10) += 1;
	if(center <= 90)//centerが最下列でない
		*(tbl + 10) += 1;
	if(place != 0)//centerが左端でない
		*(tbl - 1) += 1;
	if(place != 9)//centerが右端でない
		*(tbl + 1) += 1;
	if(size >= 2)
		midiumDrop(center, tbl, size,place);
}

