void dai(int xy[10][10],int x,int y){
    int i,j;
    for(i=x-1;i<=x+1;i++){
        for(j=y-1;j<=y+1;j++){
            if(i>=0&&j>=0&&i<=10&&j<=10)
            ++xy[i][j];
        }
    }
    if(x>=2)
        ++xy[x-2][y];
    if(x<=7)
        ++xy[x+2][y];
    if(y>=2)
        ++xy[x][y-2];
    if(y<=7)
        ++xy[x][y+2];
}

void tyu(int xy[10][10],int x,int y){
    int i,j;
    for(i=x-1;i<=x+1;i++){
        for(j=y-1;j<=y+1;j++){
            if(i>=0&&j>=0&&i<=10&&j<=10)
                ++xy[i][j];
        }
    }

}

void syo(int xy[10][10],int x,int y){
    ++xy[x][y];
    if(x>=1)
        ++xy[x-1][y];
    if(x<=8)
        ++xy[x+1][y];
    if(y>=1)
        ++xy[x][y-1];
    if(y<=8)
        ++xy[x][y+1];
}

