void s(int x,int y){
	f[x][y]=f[x][y]+1;
	if(x>=1){f[x-1][y]=f[x-1][y]+1;}
	if(x<=8){f[x+1][y]=f[x+1][y]+1;}
	if(y>=1){f[x][y-1]=f[x][y-1]+1;}
	if(y<=8){f[x][y+1]=f[x][y+1]+1;}
}

void m(int x,int y){
	s(x,y);
	if(x>=1&&y>=1){f[x-1][y-1]=f[x-1][y-1]+1;}
	if(x<=8&&y>=1){f[x+1][y-1]=f[x+1][y-1]+1;}
	if(x>=1&&y<=8){f[x-1][y+1]=f[x-1][y+1]+1;}
	if(x<=8&&y<=8){f[x+1][y+1]=f[x+1][y+1]+1;}
}

void l(int x,int y){
	m(x,y);
	if(x>=2){f[x-2][y]=f[x-2][y]+1;}
	if(x<=7){f[x+2][y]=f[x+2][y]+1;}
	if(y>=2){f[x][y-2]=f[x][y-2]+1;}
	if(y<=7){f[x][y+2]=f[x][y+2]+1;}
}

