int space[10][10]={0};/*space[y][x]と対応*/

void small(int a,int b){
	space[a][b]++;
	if(a-1>=0) space[a-1][b]++; if(a+1<=9) space[a+1][b]++;
	if(b-1>=0) space[a][b-1]++; if(b+1<=9) space[a][b+1]++;
}

void mid(int a,int b){
	small(a,b);
	if(a-1>=0) do{
				if(b-1>=0) space[a-1][b-1]++; if(b+1<=9) space[a-1][b+1]++;
	}while(0);
	if(a+1<=9) {
				if(b-1>=0) space[a+1][b-1]++; if(b+1<=9) space[a+1][b+1]++;
	}while(0);
}

void large(int a,int b){
	mid(a,b);
	if(a-2>=0) space[a-2][b]++; if(a+2<=9) space[a+2][b]++;
	if(b-2>=0) space[a][b-2]++; if(b+2<=9) space[a][b+2]++;	
}

int main(void){
	int x,y,size;
	int i,j,count,max;

	while(scanf("%d,%d,%d",&x,&y,&size)!=EOF){
	if(size==1) small(y,x);
	if(size==2) mid(y,x);
	if(size==3) large(y,x);
	};
	max=space[0][0]; count=0;
	for(i=0;i<10;i++){
		for(j=0;j<10;j++){
			if(space[i][j]==0) count++;
			if(max<space[i][j]) max=space[i][j];
		};};
		printf("%d\n%d\n",count,max);
		return (0);
}

