void large(int x, int y, int ppr[X][Y]);
void middle(int x, int y, int ppr[X][Y]);
void small(int x, int y, int ppr[X][Y]);

int main(void)
{
        int ppr[X][Y];/*紙*/
        int x = 0, y = 0;
        int i = 0, j = 0;
        int size = 0;
        int count = 0;
        int max = 0;
        for(i = 0; i < X; i++)
        {
                for(j = 0;j < Y; j++)
                {
                        ppr[i][j] = 0;
                }
        }

        while(scanf("%d,%d,%d", &x, &y, &size) != EOF)
        {
                if(size == L)
                {
                        large(x, y, ppr);
                }
                if(size == M)
                {
                        middle(x, y, ppr);
                }
                if(size == S)
                {
                        small(x, y, ppr);
                }
        }
        for(i = 0; i < X; i++)
        {
                for(j = 0;j < Y; j++)
                {
                        if( ppr[i][j] == 0 )
                        {
                                count++;
                        }
                        if(max < ppr[i][j])
                        {
                                max = ppr[i][j];
                        }
                }
        }
        printf("%d\n",count);
        printf("%d\n",max);
        return 0;
}

void large(int x, int y, int ppr[X][Y])
{
        ppr[x - 2][y]     += 1;
        ppr[x + 2][y]     += 1;
        ppr[x]    [y - 2] += 1;
        ppr[x]    [y + 2] += 1;
        ppr[x - 1][y - 1] += 1;
        ppr[x - 1][y]     += 1;
        ppr[x - 1][y + 1] += 1;
        ppr[x]    [y - 1] += 1;
        ppr[x]    [y]     += 1;
        ppr[x]    [y + 1] += 1;
        ppr[x + 1][y + 1] += 1;
        ppr[x + 1][y]     += 1;
        ppr[x + 1][y - 1] += 1;
}

void middle(int x, int y, int ppr[X][Y])
{
        ppr[x - 1][y-1]   += 1;
        ppr[x - 1][y]     += 1;
        ppr[x - 1][y+1]   += 1;
        ppr[x]    [y-1]   += 1;
        ppr[x]    [y]     += 1;
        ppr[x]    [y+1]   += 1;
        ppr[x + 1][y + 1] += 1;
        ppr[x + 1][y]     += 1;
        ppr[x + 1][y - 1] += 1;
}
void small(int x, int y, int ppr[X][Y])
{
        ppr[x + 1][y]     += 1;
        ppr[x]    [y]     += 1;
        ppr[x - 1][y]     += 1;
        ppr[x]    [y + 1] += 1;
        ppr[x]    [y - 1] += 1;
}

