void fact(int x, int y, int size){
  int size1[2] = {1,-1};
  int size2[2] = {2,-2};

  a[y][x] += 1;
  if(size == 1){
    for(i = 0 ; i < 2 ; i++){
      if(x+size1[i] >= 0 || x+size1[i] <= 10)
	a[y][x+size1[i]] += 1;
      if(y+size1[i] >= 0 || y+size1[i] <= 10)
	a[y+size1[i]][x] += 1;
    }
  }
  if(size == 2){
    for(i = 0 ; i < 2 ; i++){
      for(j = 0 ; j < 2 ; j++){
	if(x+size1[i] >= 0 || x+size1[i] <= 10 || y+size1[i] >= 0 || y+size1[i] <= 10)
	  a[y+size1[i]][x+size1[j]] += 1;
      }
    }
    for(i = 0 ; i < 2 ; i++){
      if(x+size1[i] >= 0 || x+size1[i] <= 10)
	a[y][x+size1[i]] += 1;
      if(y+size1[i] >= 0 || y+size1[i] <= 10)
	a[y+size1[i]][x] += 1;
    }
  }
  if(size == 3){
    for(i = 0 ; i < 2 ; i++){
      for(j = 0 ; j < 2 ; j++){
	if(x+size1[i] >= 0 || x+size1[i] <= 10 || y+size1[i] >= 0 || y+size1[i] <= 10)
	  a[y+size1[i]][x+size1[j]] += 1;
      }
    }
    for(i = 0 ; i < 2 ; i++){
      if(x+size1[i] >= 0 || x+size1[i] <= 10)
	a[y][x+size1[i]] += 1;
      if(y+size1[i] >= 0 || y+size1[i] <= 10)
	a[y+size1[i]][x] += 1;
    }
    for(i = 0 ; i < 2 ; i++){
      if(x+size2[i] >= 0 || x+size2[i] <= 10)
	a[y][x+size2[i]] += 1;
      if(y+size2[i] >= 0 || y+size2[i] <= 10)
	a[y+size2[i]][x] += 1;
    }
  }
}
void count(void){
  int sum = 0,x = 0;

  for(i = 0 ; i < 10 ; i++){
    for(j = 0 ; j < 10 ; j++){
      if(a[i][j] == 0)
	sum++;
      else if(x < a[i][j])
	x++;
    }
  }
  printf("%d\n%d\n",sum,x);
}

