#include <stdio.h>
#define SIZE 10
struct Ink{
	int x, y;
	int size;
};

void fill(int x, int y, int paper[SIZE][SIZE]){
	if(x < 0 || x >= SIZE){return;}
      	if(y < 0 || y >= SIZE){return;}
	paper[x][y]++;	
}

void drop(struct Ink ink, int paper[SIZE][SIZE]){
	int i,j;
	switch(ink.size){
		case 1:
			fill(ink.x  , ink.y  , paper);
			fill(ink.x  , ink.y-1, paper);
			fill(ink.x-1, ink.y  , paper);
			fill(ink.x  , ink.y+1, paper);
			fill(ink.x+1, ink.y  , paper);
		break;
		case 2:
			for(i = -1; i <= 1; i++){
				for(j = -1; j <= 1; j++){
					fill(ink.x + i, ink.y + j, paper);
				}
			}
		break;
