		largeDrop(center,tbl);
}


void littleDrop(int x ,int y ,int *tbl,int size){
//インクを落とす場所は左上(0,0)から何番目かを求める。
	int center = x + y * 10;
//(0,0)から中央へと移動させる
	tbl = (tbl + center);

//真ん中に+1
	*(tbl) += 1;
//上に+1
	if(center >= 10)
		*(tbl - 10) += 1;
	if(center <= 90)
		*(tbl + 10) += 1;
	if(center % 10 != 0)
		*(tbl - 1) += 1;
	if(center % 10 != 9)
		*(tbl + 1) += 1;
	if(size >= 2)
		midiumDrop(center, tbl, size);
}



