void di(int map[10][10], int x, int y)
{
	int i, j;
	for (i = y - 1; i <= y + 1; i++){
		for (j = x - 1; j <= x + 1; j++){
			if (0 <= i && i < 10 && 0 <= j && j < 10){
				map[i][j]++;
			}
		}
	}
}

int main(void)
{
