	int wc = 0, max_i = 0;
	int map[10][10] = {0};

	while (scanf("%d,%d,%d", &x, &y, &s) != EOF){
		switch (s){
		  case 1:
		  	di(map, x, y);
			map[y - 1][x - 1]--;
			map[y - 1][x + 1]--;
			map[y + 1][x - 1]--;
			map[y + 1][x + 1]--;
			break;

		  case 2:
		  	di(map, x, y);
			break;

		  case 3:
		  	di(map, x, y);
			map[y - 2][x]++;
			map[y][x - 2]++;
			map[y][x + 2]++;
			map[y + 2][x]++;
			break;
		}
	}

	for (y = 0; y < 10; y++){
