int board[10][10];

void init(void)
{
	int i, j;

	for (i=0; i<10; i++) {
		for (j=0; j<10; j++) {
			board[i][j] = 0;
		}
	}
}

int main(void)
{
	int i, j, ii, jj, size;
	init();

	while (scanf("%d,%d,%d", &i, &j, &size) != EOF) {
		switch (size) {
			case 1:
				board[i][j] += 1; 
				if (i-1 >= 0) board[i-1][j] += 1; 
				if (i+1 < 10) board[i+1][j] += 1; 
				if (j-1 >= 0)  board[i][j-1] += 1; 
				if (j+1 < 10) board[i][j+1] += 1;
				break;

			case 3:
				if (i-2 >= 0)  board[i-2][j] += 1; 
				if (i+2 < 10) board[i+2][j] += 1; 
				if (j-2 >= 0)  board[i][j-2] += 1; 
				if (j+2 < 10) board[i][j+2] += 1;

			case 2:
				for (ii=i-1; ii<=i+1; ii++) {
					for (jj=j-1; jj<=j+1; jj++) {
						if (ii => 0 && ii < 10 && jj => 0 && jj < 10) board[ii][jj] += 1;
					}
				}
		}
	}

	int zero = 0, max = 0;
	for (i=0; i<10; i++) {
		for (j=0; j<10; j++) {
			if (board[i][j] == 0) zero += 1;
			else if (max < board[i][j]) max = board[i][j];
		}
	}

	printf("%d\n%d\n", zero, max);
	return 0;
}

