int main(void){
  int paper[10][10];
  int x,y,q,i,j,count;
  int max=0;

  for(j=0;j<10;j++){
    for(i=0;i<10;i++){
      paper[i][j]=0;
    }
  }

  while(scanf("%d,%d,%d",&x,&y,&q)!=EOF){

    if(q==1){
      small(paper,x,y);
    }else if(q==2){
      mid(paper,x,y);
    }else{
      large(paper,x,y);
    }
  }
  for(j=0;j<10;j++){
    for(i=0;i<10;i++){
      if(paper[j][i]==0){
	count++;
