void inkpaint1(int x, int y);
void inkpaint2(int x, int y);
void inkpaint3(int x, int y);
void result(void);

int main(void)
{
    int x,y,s;

    while (scanf("%d,%d,%d", &x, &y, &s) != EOF)
    {
        switch (s)
        {
            case 1:
            inkpaint1(x+2, y+2);
            break;

            case 2:
            inkpaint2(x+2, y+2);
            break;

            case 3:
            inkpaint3(x+2, y+2);
            break;
        }
    }
    result();

    return (0);
}
void inkpaint1(int x, int y)
{
    map[x][y] += 1;
    map[x - 1][y] += 1;
    map[x + 1][y] += 1;
    map[x][y + 1] += 1;
    map[x][y - 1] += 1;
}
void inkpaint2(int x, int y)
{
    inkpaint1(x, y);
    map[x - 1][y - 1] += 1;
    map[x - 1][y + 1] += 1;
    map[x + 1][y - 1] += 1;
    map[x + 1][y + 1] += 1;
}
void inkpaint3(int x, int y)
{
    inkpaint2(x, y);
    map[x][y - 2] += 1;
    map[x][y + 2] += 1;
    map[x - 2][y] += 1;
    map[x + 2][y] += 1;
}
void result(void)
{
    int i,j;
    int white,black;

    white = black = 0;

    for (i = 2; i < 12; i++){
        for (j = 2; j < 12; j++){
            if(map[i][j] == 0){
                white++;
            }
            if (black < map[i][j]){
                black = map[i][j];
            }
        }
    }
    printf("%d\n", white);
    printf("%d\n", black);
}

