		map[y][x-1]++;
	if(x < 9)
		map[y][x+1]++;
	if(y >= 1)
		map[y-1][x]++;
	if(y < 9)
		map[y+1][x]++;

}
void Drop_2(int x,int y){

	if(x >= 1){
		map[y][x-1]++;

		if(y >= 1)
			map[y-1][x-1]++;
		if(y < 9)
			map[y+1][x-1]++;

	}
	if(y >= 1)
		map[y-1][x]++;
	if(y < 9)
		map[y+1][x]++;

	if(x < 9){
		map[y][x+1]++;

		if(y >= 1)
			map[y-1][x+1]++;
		if(y < 9)
			map[y+1][x+1]++;

	}


}
void Drop_3(int x,int y){

	Drop_2(x,y);

	if(x >= 2)
		map[y][x-2]++;
	if(x < 8)
		map[y][x+2]++;
	if(y >= 2)
		map[y-2][x]++;
	if(y < 8)
		map[y+2][x]++;


}

void Drop(int x, int y, int size){

	map[y][x] ++;

	switch(size){

	case 1:
		Drop_1(x,y);
		break;

	case 2:
		Drop_2(x,y);
		break;

	case 3:
		Drop_3(x,y);
		break;
	}

}


